import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { usePortfolio } from "./PortfolioProvider";

const StockPicker = () => {
  const [ticker, setTicker] = useState("");
  const [stockDetails, setStockDetails] = useState(null);
  const [shares, setShares] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [totalCost, setTotalCost] = useState(0);
  const [loading, setLoading] = useState(false);

  const { refreshPortfolio } = usePortfolio();
  const navigate = useNavigate();

  const API_KEY = "A2XELEM8W6WSTE3W";
  const API_BASE_URL = "https://www.alphavantage.co/query";

  const isValidTicker = (ticker) => /^[A-Z]{1,5}$/.test(ticker); // Tickers are 1â€“5 uppercase letters
  const isValidShares = (shares) => /^\d+$/.test(shares) && parseInt(shares, 10) > 0; // Positive integers only

  const searchStock = async () => {
    if (!isValidTicker(ticker)) {
      setErrorMessage("Please enter a valid stock ticker (1â€“5 uppercase letters).");
      return;
    }

    try {
      setLoading(true); // Show loading indicator
      const response = await axios.get(API_BASE_URL, {
        params: {
          function: "SYMBOL_SEARCH",
          keywords: ticker,
          apikey: API_KEY,
        },
      });
      const bestMatch = response.data.bestMatches?.[0];
      if (bestMatch) {
        const symbol = bestMatch["1. symbol"];
        const price = await fetchStockPrice(symbol);

        setStockDetails({
          symbol,
          name: bestMatch["2. name"],
          region: bestMatch["4. region"],
          currency: bestMatch["8. currency"],
          price: price || "N/A",
        });
        setErrorMessage("");
      } else {
        setStockDetails(null);
        setErrorMessage("Stock not found. Please check the ticker.");
      }
    } catch (error) {
      setStockDetails(null);
      setErrorMessage("Error fetching stock data. Please try again.");
    } finally {
      setLoading(false); // Hide loading indicator
    }
  };

  const fetchStockPrice = async (symbol) => {
    try {
      const response = await axios.get(API_BASE_URL, {
        params: {
          function: "GLOBAL_QUOTE",
          symbol,
          apikey: API_KEY,
        },
      });
      return parseFloat(response.data["Global Quote"]["05. price"]).toFixed(2);
    } catch (error) {
      console.error("Error fetching stock price:", error);
      return null;
    }
  };

  const addToPortfolio = async () => {
    if (!isValidShares(shares)) {
      setErrorMessage("Please enter a valid number of shares (positive integer).");
      return;
    }

    try {
      const token = localStorage.getItem("token");
      const response = await axios.post(
        "http://127.0.0.1:5000/api/portfolio/add",
        { ticker: stockDetails.symbol, shares: parseInt(shares, 10), price: sto  },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      if (response.status === 200) {
        setSuccessMessage(response.data.message);
        refreshPortfolio(); // Refresh the portfolio
        setShares(""); // Reset shares input
      } else {
        throw new Error(response.data.message || "Unexpected error occurred.");
      }
    } catch (error) {
      setSuccessMessage("");
      setErrorMessage("Error adding stock to portfolio.");
      console.error("Error adding stock to portfolio:", error);
    }
  };

  useEffect(() => {
    if (stockDetails?.price && shares) {
      setTotalCost((parseFloat(stockDetails.price) * parseInt(shares, 10)).toFixed(2));
    } else {
      setTotalCost(0);
    }
  }, [stockDetails, shares]);

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Add a Stock to Your Portfolio</h2>
      <div style={styles.inputGroup}>
        <input
          type="text"
          placeholder="Enter Stock Ticker (e.g., AAPL)"
          value={ticker}
          onChange={(e) => setTicker(e.target.value.toUpperCase())}
          style={styles.input}
        />
        <button onClick={searchStock} style={styles.button}>
          Search
        </button>
      </div>

      {loading && <p style={styles.loading}>Loading...</p>}

      {errorMessage && <p style={styles.error}>{errorMessage}</p>}

      {stockDetails && (
        <div style={styles.stockDetails}>
          <h4 style={styles.subHeading}>Stock Details:</h4>
          <p><strong>Symbol:</strong> {stockDetails.symbol}</p>
          <p><strong>Company:</strong> {stockDetails.name}</p>
          <p><strong>Region:</strong> {stockDetails.region}</p>
          <p><strong>Currency:</strong> {stockDetails.currency}</p>
          <p><strong>Current Price:</strong> ${stockDetails.price}</p>
        </div>
      )}

      {stockDetails && (
        <div style={styles.inputGroup}>
          <input
            type="number"
            placeholder="Enter Number of Shares"
            value={shares}
            onChange={(e) => setShares(e.target.value)}
            style={styles.input}
          />
          <p><strong>Total Cost:</strong> ${totalCost || 0}</p>
          <button onClick={addToPortfolio} style={styles.button}>
            Add to Portfolio
          </button>
        </div>
      )}

      {successMessage && <p style={styles.success}>{successMessage}</p>}

      <button
        onClick={() => navigate("/dashboard")}
        style={{ ...styles.button, background: "#6c757d" }}
      >
        Go Back to Dashboard
      </button>
    </div>
  );
};

const styles = {
  container: {
    padding: "20px",
    fontFamily: "Arial, sans-serif",
    maxWidth: "600px",
    margin: "auto",
  },
  heading: {
    fontSize: "24px",
    fontWeight: "bold",
    marginBottom: "20px",
  },
  subHeading: {
    fontSize: "18px",
    fontWeight: "bold",
    marginBottom: "10px",
  },
  inputGroup: {
    marginBottom: "20px",
  },
  input: {
    padding: "10px",
    marginRight: "10px",
    width: "70%",
  },
  button: {
    padding: "10px 20px",
    background: "#007BFF",
    color: "white",
    border: "none",
    cursor: "pointer",
  },
  loading: {
    color: "#007BFF",
  },
  error: {
    color: "red",
  },
  success: {
    color: "green",
  },
  stockDetails: {
    border: "1px solid #ddd",
    padding: "10px",
    borderRadius: "5px",
    background: "#f9f9f9",
  },
};

export default StockPicker;
