import React, { useState } from "react";
import axios from "axios";

const StockPicker = ({ refreshPortfolio }) => {
  const [ticker, setTicker] = useState("");
  const [stockDetails, setStockDetails] = useState(null);
  const [shares, setShares] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  const searchStock = async () => {
    try {
      const response = await axios.get(`https://api.marketstack.com/v1/tickers/${ticker}`);
      setStockDetails(response.data);
      setErrorMessage("");
    } catch (error) {
      c
      setStockDetails(null);
      setErrorMessage("Stock not found. Please check the ticker.");
    }
  };

  const addToPortfolio = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.post(
        "http://127.0.0.1:5000/api/portfolio/add",
        { ticker, shares: parseInt(shares, 10) },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setSuccessMessage(response.data.message);
      setErrorMessage("");
      setShares(""); // Clear shares input
      refreshPortfolio(); // Refresh portfolio in the parent component
    } catch (error) {
      setSuccessMessage("");
      setErrorMessage(error.response?.data?.message || "Error adding stock.");
    }
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      <h3>Add a Stock to Your Portfolio</h3>
      <div style={{ marginBottom: "20px" }}>
        <input
          type="text"
          placeholder="Enter Stock Ticker (e.g., AAPL)"
          value={ticker}
          onChange={(e) => setTicker(e.target.value.toUpperCase())}
          style={{ padding: "10px", marginRight: "10px", width: "300px" }}
        />
        <button onClick={searchStock} style={{ padding: "10px 20px" }}>
          Search
        </button>
      </div>

      {errorMessage && <p style={{ color: "red" }}>{errorMessage}</p>}

      {stockDetails && (
        <div style={{ marginBottom: "20px" }}>
          <h4>Stock Details:</h4>
          <p><strong>Company:</strong> {stockDetails.name}</p>
          <p><strong>Current Price:</strong> ${stockDetails.last_trade_price.toFixed(2)}</p>
        </div>
      )}

      {stockDetails && (
        <div>
          <input
            type="number"
            placeholder="Enter Number of Shares"
            value={shares}
            onChange={(e) => setShares(e.target.value)}
            style={{ padding: "10px", marginRight: "10px", width: "150px" }}
          />
          <button onClick={addToPortfolio} style={{ padding: "10px 20px" }}>
            Add to Portfolio
          </button>
        </div>
      )}

      {successMessage && <p style={{ color: "green" }}>{successMessage}</p>}
    </div>
  );
};

export default StockPicker;
