import React, { useEffect, useState } from "react";
import axios from "axios";

const Dashboard = () => {
  const [portfolio, setPortfolio] = useState([]);
  const [totalValue, setTotalValue] = useState(0);

  const fetchPortfolio = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get("http://127.0.0.1:5000/api/portfolio/value", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const portfolioData = Array.isArray(response.data.portfolio) ? response.data.portfolio : [];
      setPortfolio(portfolioData);
      setTotalValue(response.data.total_value || 0);
    } catch (error) {
      console.error("Error fetching portfolio:", error.message);
    }
  };

  useEffect(() => {
    fetchPortfolio();
  }, []);

  return (
    <div>
      <h2>Your Portfolio</h2>
      <p><strong>Total Portfolio Value:</strong> ${totalValue.toLocaleString()}</p>

      <h3>Portfolio Details</h3>
      {portfolio.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>Ticker</th>
              <th>Shares</th>
              <th>Current Price</th>
              <th>Previous Close</th>
              <th>Daily Change</th>
              <th>Daily Change (%)</th>
              <th>Total Value</th>
            </tr>
          </thead>
          <tbody>
            {portfolio.map((stock) => (
              <tr key={stock.ticker}>
                <td>{stock.ticker}</td>
                <td>{stock.shares}</td>
                <td>${stock.current_price.toFixed(2)}</td>
                <td>${stock.previous_close.toFixed(2)}</td>
                <td
                  style={{
                    color: stock.daily_change > 0 ? "green" : stock.daily_change < 0 ? "red" : "black",
                  }}
                >
                  ${stock.daily_change.toFixed(2)}
                </td>
                <td
                  style={{
                    color: stock.daily_change_percentage > 0 ? "green" : stock.daily_change_percentage < 0 ? "red" : "black",
                  }}
                >
                  {stock.daily_change_percentage.toFixed(2)}%
                </td>
                <td>${stock.value.toFixed(2)}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No portfolio data available.</p>
      )}
    </div>
  );
};

export default Dashboard;
