import React, { useEffect, useState } from "react";
import axios from "axios";
import { Line, Pie } from "react-chartjs-2";

const Dashboard = () => {
  const [portfolio, setPortfolio] = useState([]); // Portfolio data
  const [totalValue, setTotalValue] = useState(0); // Total portfolio value


  const renderPortfolioLineChart = (portfolio, historicalData) => {
    // Aggregate total value for each date
    const dates = Object.keys(historicalData[portfolio[0].ticker] || {});
    const totalValues = dates.map((date) =>
      portfolio.reduce((sum, stock) => {
        const history = historicalData[stock.ticker];
        const priceOnDate = history?.find((entry) => entry.Date === date)?.Close || 0;
        return sum + priceOnDate * stock.shares;
      }, 0)
    );

    const data = {
      labels: dates,
      datasets: [
        {
          label: "Total Portfolio Value",
          data: totalValues,
          borderColor: "rgba(75,192,192,1)",
          backgroundColor: "rgba(75,192,192,0.2)",
          tension: 0.2, // Smooth curves
        },
      ],
    };

    return <Line data={data} />;
  };



  const fetchPortfolio = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get("http://127.0.0.1:5000/api/portfolio/value", {
        headers: { Authorization: `Bearer ${token}` },
      });
      console.log("Portfolio API Response:", response.data); // Debug log
      setPortfolio(Array.isArray(response.data.portfolio) ? response.data.portfolio : []);
      setTotalValue(response.data.total_value || 0);
    } catch (error) {
      console.error("Error fetching portfolio:", error.response?.data?.message || error.message);
    }
  };

  useEffect(() => {
    fetchPortfolio();
  }, []);

  useEffect(() => {
    console.log("Portfolio State:", portfolio); // Debug log
  }, [portfolio]);

  return (
    <div>
      <h2>Your Portfolio</h2>
      <p><strong>Total Portfolio Value:</strong> ${totalValue.toLocaleString()}</p>
      {Array.isArray(portfolio) && portfolio.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>Ticker</th>
              <th>Shares</th>
              <th>Current Price</th>
              <th>Total Value</th>
            </tr>
          </thead>
          <tbody>
            {portfolio.map((stock) => (
              <tr key={stock.ticker}>
                <td>{stock.ticker}</td>
                <td>{stock.shares}</td>
                <td>${stock.current_price}</td>
                <td>${stock.value}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No portfolio data available.</p>
      )}
    </div>
  );
};

export default Dashboard;
