import React, { useEffect, useState } from "react";
import axios from "axios";
import StockPicker from "./StockPicker";
import {
  Chart as ChartJS,
  ArcElement,
  LineElement,
  CategoryScale,
  LinearScale,
  TimeScale,
  PointElement,
  Legend,
  Tooltip,
} from "chart.js";
import { Pie, Line } from "react-chartjs-2";
import { useNavigate } from "react-router-dom";

// Register Chart.js components
ChartJS.register(
  ArcElement,
  LineElement,
  CategoryScale,
  LinearScale,
  TimeScale,
  PointElement,
  Legend,
  Tooltip
);

const Dashboard = () => {
  const [portfolio, setPortfolio] = useState([]);
  const [totalValue, setTotalValue] = useState(0);
  const [historicalData, setHistoricalData] = useState({});
  const history = useHistory();

  const fetchHistoricalData = async (ticker) => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get(`http://127.0.0.1:5000/api/stock/history/${ticker}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setHistoricalData((prev) => ({
        ...prev,
        [ticker]: response.data,
      }));
    } catch (error) {
      console.error(`Error fetching historical data for ${ticker}:`, error.message);
    }
  };

  const fetchPortfolio = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get("http://127.0.0.1:5000/api/portfolio/value", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const portfolioData = Array.isArray(response.data.portfolio) ? response.data.portfolio : [];
      setPortfolio(portfolioData);
      setTotalValue(response.data.total_value || 0);

      // Fetch historical data for all stocks
      portfolioData.forEach((stock) => fetchHistoricalData(stock.ticker));
    } catch (error) {
      console.error("Error fetching portfolio:", error.message);
    }
  };

  useEffect(() => {
    fetchPortfolio();
  }, []);

  const renderPortfolioPieChart = (portfolio) => {
    const data = {
      labels: portfolio.map((stock) => stock.ticker),
      datasets: [
        {
          data: portfolio.map((stock) => stock.value),
          backgroundColor: [
            "rgba(75,192,192,0.6)",
            "rgba(255,99,132,0.6)",
            "rgba(54,162,235,0.6)",
            "rgba(255,206,86,0.6)",
            "rgba(153,102,255,0.6)",
          ],
          borderWidth: 1,
        },
      ],
    };

    const options = {
      responsive: true,
      maintainAspectRatio: false, // Allow custom dimensions
    };

    return (
      <div style={{ width: "400px", height: "400px", margin: "0 auto" }}>
        <Pie key="portfolio-pie-chart" data={data} options={options} />
      </div>
    );
  };

  const renderStockLineChart = (ticker, data) => {
    const chartData = {
      labels: data.map((point) => point.Date),
      datasets: [
        {
          label: `${ticker} Performance`,
          data: data.map((point) => point.Close),
          borderColor: "rgba(54, 162, 235, 1)",
          backgroundColor: "rgba(54, 162, 235, 0.2)",
          tension: 0.2,
        },
      ],
    };

    const options = {
      responsive: true,
      maintainAspectRatio: false, // Allow custom dimensions
    };

    return (
      <div style={{ width: "500px", height: "300px", margin: "20px auto" }}>
        <Line key={`line-chart-${ticker}`} data={chartData} options={options} />
      </div>
    );
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      <h2>Your Portfolio</h2>
      <button
        onClick={() => history.push("/stock-picker")}
        style={{ padding: "10px 20px", marginBottom: "20px" }}
      >
        Add Stocks
      </button>
      <p><strong>Total Portfolio Value:</strong> ${totalValue.toLocaleString()}</p>

      <StockPicker refreshPortfolio={fetchPortfolio} />

      <div style={{ marginBottom: "40px", textAlign: "center" }}>
        <h3>Portfolio Distribution</h3>
        {portfolio.length > 0 && renderPortfolioPieChart(portfolio)}
      </div>

      <h3>Portfolio Details</h3>
      {portfolio.length > 0 ? (
        <table style={{ width: "100%", borderCollapse: "collapse", marginBottom: "40px" }}>
          <thead>
            <tr style={{ backgroundColor: "#f4f4f4" }}>
              <th style={{ padding: "10px", border: "1px solid #ddd" }}>Ticker</th>
              <th style={{ padding: "10px", border: "1px solid #ddd" }}>Shares</th>
              <th style={{ padding: "10px", border: "1px solid #ddd" }}>Current Price</th>
              <th style={{ padding: "10px", border: "1px solid #ddd" }}>Previous Close</th>
              <th style={{ padding: "10px", border: "1px solid #ddd" }}>Daily Change</th>
              <th style={{ padding: "10px", border: "1px solid #ddd" }}>Daily Change (%)</th>
              <th style={{ padding: "10px", border: "1px solid #ddd" }}>Total Value</th>
            </tr>
          </thead>
          <tbody>
            {portfolio.map((stock) => (
              <tr key={stock.ticker}>
                <td style={{ padding: "10px", border: "1px solid #ddd" }}>{stock.ticker}</td>
                <td style={{ padding: "10px", border: "1px solid #ddd" }}>{stock.shares}</td>
                <td style={{ padding: "10px", border: "1px solid #ddd" }}>${stock.current_price.toFixed(2)}</td>
                <td style={{ padding: "10px", border: "1px solid #ddd" }}>${stock.previous_close.toFixed(2)}</td>
                <td
                  style={{
                    padding: "10px",
                    border: "1px solid #ddd",
                    color: stock.daily_change > 0 ? "green" : stock.daily_change < 0 ? "red" : "black",
                  }}
                >
                  ${stock.daily_change.toFixed(2)}
                </td>
                <td
                  style={{
                    padding: "10px",
                    border: "1px solid #ddd",
                    color: stock.daily_change_percentage > 0
                      ? "green"
                      : stock.daily_change_percentage < 0
                      ? "red"
                      : "black",
                  }}
                >
                  {stock.daily_change_percentage.toFixed(2)}%
                </td>
                <td style={{ padding: "10px", border: "1px solid #ddd" }}>${stock.value.toFixed(2)}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No portfolio data available.</p>
      )}

      <h3>Stock Performance Over Time</h3>
      <div
        style={{
          display: "flex",
          flexWrap: "wrap",
          justifyContent: "space-around",
          gap: "20px",
        }}
      >
        {portfolio.map((stock) => {
          const data = historicalData[stock.ticker];
          return data ? (
            <div
              key={`chart-container-${stock.ticker}`}
              style={{
                padding: "10px",
                boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
                backgroundColor: "#fff",
                borderRadius: "8px",
              }}
            >
              <h4 style={{ textAlign: "center" }}>{stock.ticker}</h4>
              {renderStockLineChart(stock.ticker, data)}
            </div>
          ) : (
            <p key={`loading-${stock.ticker}`}>Loading historical data for {stock.ticker}...</p>
          );
        })}
      </div>
    </div>
  );
};

export default Dashboard;
