import React, { useEffect, useState } from "react";
import {
  Chart as ChartJS,
  LineElement,
  CategoryScale,
  LinearScale,
  TimeScale,
  PointElement,
  Legend,
  Tooltip,
} from "chart.js";
import { Line } from "react-chartjs-2";
import "chartjs-adapter-date-fns";
import axios from "axios";

ChartJS.register(LineElement, CategoryScale, LinearScale, TimeScale, PointElement, Legend, Tooltip);

const Dashboard = () => {
  const [portfolio, setPortfolio] = useState([]);
  const [stockHistory, setStockHistory] = useState({});

  const fetchPortfolio = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get("http://127.0.0.1:5000/api/portfolio/value", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setPortfolio(response.data);
    } catch (error) {
      console.error("Error fetching portfolio:", error.response?.data?.message);
    }
  };

  const fetchStockHistory = async (ticker) => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get(`http://127.0.0.1:5000/api/stock/history/${ticker}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setStockHistory((prev) => ({
        ...prev,
        [ticker]: response.data,
      }));
    } catch (error) {
      console.error(`Failed to fetch history for ${ticker}:`, error.response?.data?.message);
    }
  };

  useEffect(() => {
    fetchPortfolio();
  }, []);

  useEffect(() => {
    portfolio.forEach((stock) => fetchStockHistory(stock.stock_ticker));
  }, [portfolio]);

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: "top",
      },
    },
    scales: {
      x: {
        type: "time",
        time: {
          unit: "month",
        },
      },
    },
  };

  return (
    <div>
      <h2>Your Portfolio</h2>
      {portfolio.map((stock) => {
        const history = stockHistory[stock.stock_ticker];
        if (!history) return <p key={stock.stock_ticker}>Loading data for {stock.stock_ticker}...</p>;

        const data = {
          labels: history.map((point) => point.Date),
          datasets: [
            {
              label: `${stock.stock_ticker} Price`,
              data: history.map((point) => point.Close),
              borderColor: "rgba(75,192,192,1)",
              backgroundColor: "rgba(75,192,192,0.2)",
              tension: 0.2,
            },
          ],
        };

        return (
          <div key={stock.stock_ticker}>
            <h4>{stock.stock_ticker}</h4>
            <Line data={data} options={options} />
          </div>
        );
      })}
    </div>
  );
};

export default Dashboard;
