import React, { useState, useEffect } from "react";
import axios from "axios";

const StockPicker = ({ refreshPortfolio }) => {
  const [ticker, setTicker] = useState("");
  const [stockDetails, setStockDetails] = useState(null);
  const [shares, setShares] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [portfolio, setPortfolio] = useState([]);
  const [totalCost, setTotalCost] = useState(0);

  const API_KEY = "your_alpha_vantage_api_key"; // Replace with your Alpha Vantage API key
  const API_BASE_URL = "https://www.alphavantage.co/query";

  // Fetch the user's portfolio
  const fetchPortfolio = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get("http://127.0.0.1:5000/api/portfolio", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setPortfolio(response.data);
    } catch (error) {
      console.error("Error fetching portfolio:", error);
    }
  };

  // Search for a stock
  const searchStock = async () => {
    try {
      const response = await axios.get(API_BASE_URL, {
        params: {
          function: "SYMBOL_SEARCH",
          keywords: ticker,
          apikey: API_KEY,
        },
      });
      const bestMatch = response.data.bestMatches?.[0];
      if (bestMatch) {
        const symbol = bestMatch["1. symbol"];
        const price = await fetchStockPrice(symbol);

        setStockDetails({
          symbol,
          name: bestMatch["2. name"],
          region: bestMatch["4. region"],
          currency: bestMatch["8. currency"],
          price: price || "N/A",
        });
        setErrorMessage("");
      } else {
        setStockDetails(null);
        setErrorMessage("Stock not found. Please check the ticker.");
      }
    } catch (error) {
      setStockDetails(null);
      setErrorMessage("Error fetching stock data. Please try again.");
    }
  };

  // Fetch the stock price
  const fetchStockPrice = async (symbol) => {
    try {
      const response = await axios.get(API_BASE_URL, {
        params: {
          function: "GLOBAL_QUOTE",
          symbol,
          apikey: API_KEY,
        },
      });
      return parseFloat(response.data["Global Quote"]["05. price"]).toFixed(2);
    } catch (error) {
      console.error("Error fetching stock price:", error);
      return null;
    }
  };

  // Add stock to the portfolio
  const addToPortfolio = async () => {
    if (portfolio.some((stock) => stock.stock_ticker === stockDetails.symbol)) {
      setErrorMessage("Stock is already in your portfolio.");
      return;
    }

    try {
      const token = localStorage.getItem("token");
      await axios.post(
        "http://127.0.0.1:5000/api/portfolio/add",
        { ticker: stockDetails.symbol, shares: parseInt(shares, 10) },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setSuccessMessage(`${shares} shares of ${stockDetails.symbol} added successfully!`);
      setErrorMessage("");
      setShares("");
      setTotalCost(0);
      refreshPortfolio(); // Refresh the portfolio in the parent component
      fetchPortfolio(); // Update the local portfolio state
    } catch (error) {
      setSuccessMessage("");
      setErrorMessage("Error adding stock to portfolio.");
    }
  };

  // Calculate the total cost
  useEffect(() => {
    if (stockDetails?.price && shares) {
      setTotalCost((parseFloat(stockDetails.price) * parseInt(shares, 10)).toFixed(2));
    } else {
      setTotalCost(0);
    }
  }, [stockDetails, shares]);

  useEffect(() => {
    fetchPortfolio();
  }, []);

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      <h2>Add a Stock to Your Portfolio</h2>
      <div style={{ marginBottom: "20px" }}>
        <input
          type="text"
          placeholder="Enter Stock Ticker (e.g., AAPL)"
          value={ticker}
          onChange={(e) => setTicker(e.target.value.toUpperCase())}
          style={{ padding: "10px", marginRight: "10px", width: "300px" }}
        />
        <button onClick={searchStock} style={{ padding: "10px 20px" }}>
          Search
        </button>
      </div>

      {errorMessage && <p style={{ color: "red" }}>{errorMessage}</p>}

      {stockDetails && (
        <div style={{ marginBottom: "20px" }}>
          <h4>Stock Details:</h4>
          <p><strong>Symbol:</strong> {stockDetails.symbol}</p>
          <p><strong>Company:</strong> {stockDetails.name}</p>
          <p><strong>Region:</strong> {stockDetails.region}</p>
          <p><strong>Currency:</strong> {stockDetails.currency}</p>
          <p><strong>Current Price:</strong> ${stockDetails.price}</p>
        </div>
      )}

      {stockDetails && (
        <div>
          <input
            type="number"
            placeholder="Enter Number of Shares"
            value={shares}
            onChange={(e) => setShares(e.target.value)}
            style={{ padding: "10px", marginRight: "10px", width: "150px" }}
          />
          <p><strong>Total Cost:</strong> ${totalCost || 0}</p>
          <button onClick={addToPortfolio} style={{ padding: "10px 20px" }}>
            Add to Portfolio
          </button>
        </div>
      )}

      {successMessage && <p style={{ color: "green" }}>{successMessage}</p>}
    </div>
  );
};

export default StockPicker;
