import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  Chart as ChartJS,
  ArcElement,
  LineElement,
  CategoryScale,
  LinearScale,
  TimeScale,
  PointElement,
  Legend,
  Tooltip,
} from "chart.js";
import { Pie, Line } from "react-chartjs-2";

// Register required components
try {
  ChartJS.register(
    ArcElement,
    LineElement,
    CategoryScale,
    LinearScale,
    TimeScale,
    PointElement,
    Legend,
    Tooltip
  );
  console.log("Chart.js components registered successfully.");
} catch (error) {
  console.error("Error registering Chart.js components:", error);
}

const Dashboard = () => {
  const [portfolio, setPortfolio] = useState([]);
  const [totalValue, setTotalValue] = useState(0);
  const [historicalData, setHistoricalData] = useState({});

  const fetchHistoricalData = async (ticker) => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get(`http://127.0.0.1:5000/api/stock/history/${ticker}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setHistoricalData((prev) => ({
        ...prev,
        [ticker]: response.data,
      }));
    } catch (error) {
      console.error(`Error fetching historical data for ${ticker}:`, error.message);
    }
  };

  const fetchPortfolio = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get("http://127.0.0.1:5000/api/portfolio/value", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const portfolioData = Array.isArray(response.data.portfolio) ? response.data.portfolio : [];
      setPortfolio(portfolioData);
      setTotalValue(response.data.total_value || 0);

      // Fetch historical data for all stocks
      portfolioData.forEach((stock) => fetchHistoricalData(stock.ticker));
    } catch (error) {
      console.error("Error fetching portfolio:", error.message);
    }
  };

  useEffect(() => {
    fetchPortfolio();
  }, []);

  const renderPortfolioPieChart = (portfolio) => {
    const data = {
      labels: portfolio.map((stock) => stock.ticker),
      datasets: [
        {
          data: portfolio.map((stock) => stock.value),
          backgroundColor: [
            "rgba(75,192,192,0.6)",
            "rgba(255,99,132,0.6)",
            "rgba(54,162,235,0.6)",
            "rgba(255,206,86,0.6)",
            "rgba(153,102,255,0.6)",
          ],
          borderWidth: 1,
        },
      ],
    };
    return <Pie key="portfolio-pie-chart" data={data} />;
  };

  const renderStockLineChart = (ticker, data) => {
    const chartData = {
      labels: data.map((point) => point.Date), // Ensure 'Date' is a string
      datasets: [
        {
          label: `${ticker} Performance`,
          data: data.map((point) => point.Close), // Ensure 'Close' is a number
          borderColor: "rgba(54, 162, 235, 1)",
          backgroundColor: "rgba(54, 162, 235, 0.2)",
          tension: 0.2,
        },
      ],
    };

    return <Line key={`line-chart-${ticker}`} data={chartData} />;
  };

  return (
    <div>
      <h2>Your Portfolio</h2>
      <p><strong>Total Portfolio Value:</strong> ${totalValue.toLocaleString()}</p>

      <div style={{ marginBottom: "20px" }}>
        <h3>Portfolio Distribution</h3>
        {portfolio.length > 0 && renderPortfolioPieChart(portfolio)}
      </div>

      <h3>Stock Performance Over Time</h3>
      {portfolio.map((stock) => {
        const data = historicalData[stock.ticker];
        return data ? (
          <div key={`chart-container-${stock.ticker}`} style={{ marginBottom: "30px" }}>
            <h4>{stock.ticker}</h4>
            {renderStockLineChart(stock.ticker, data)}
          </div>
        ) : (
          <p key={`loading-${stock.ticker}`}>Loading historical data for {stock.ticker}...</p>
        );
      })}
    </div>
  );
};

export default Dashboard;
