import React, { useState, useEffect } from "react";
import axios from "axios";
import { Line, Pie, Bar } from "react-chartjs-2";
import { Chart as ChartJS, LineElement, CategoryScale, LinearScale, PointElement, ArcElement, BarElement, Tooltip, Legend } from "chart.js";

// Register Chart.js components
ChartJS.register(LineElement, CategoryScale, LinearScale, PointElement, ArcElement, BarElement, Tooltip, Legend);

const Dashboard = () => {
  const [portfolio, setPortfolio] = useState([]);
  const [analytics, setAnalytics] = useState(null);

  const fetchPortfolio = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get("http://127.0.0.1:5000/api/portfolio", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setPortfolio(response.data.portfolio);
      setAnalytics(response.data.analytics);
    } catch (error) {
      console.error("Error fetching portfolio:", error);
    }
  };

  useEffect(() => {
    fetchPortfolio();
  }, []);

  if (!analytics) {
    return <p>Loading analytics...</p>;
  }

  const { sector_allocation, historical_performance, top_performers } = analytics;

  // Prepare chart data
  const lineData = {
    labels: historical_performance.dates,
    datasets: [
      {
        label: "Portfolio Value Over Time",
        data: historical_performance.values,
        borderColor: "#007BFF",
        backgroundColor: "rgba(0, 123, 255, 0.2)",
        tension: 0.3,
      },
    ],
  };

  const pieData = {
    labels: Object.keys(sector_allocation),
    datasets: [
      {
        data: Object.values(sector_allocation),
        backgroundColor: ["#007BFF", "#28A745", "#FFC107", "#DC3545"],
      },
    ],
  };

  const barData = {
    labels: top_performers.map((stock) => stock.ticker),
    datasets: [
      {
        label: "Performance (%)",
        data: top_performers.map((stock) => stock.performance),
        backgroundColor: "#17A2B8",
      },
    ],
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      <h2>Dashboard</h2>

      <div style={{ marginBottom: "40px" }}>
        <h4>Portfolio Value Over Time</h4>
        <Line data={lineData} />
      </div>

      <div style={{ marginBottom: "40px" }}>
        <h4>Sector Distribution</h4>
        <Pie data={pieData} />
      </div>

      <div style={{ marginBottom: "40px" }}>
        <h4>Top Performers</h4>
        <Bar data={barData} />
      </div>
    </div>
  );
};

export default Dashboard;
