// /frontend/src/Dashboard.js
import React, { useEffect, useState } from "react";
import axios from "axios";

const Dashboard = () => {
  const [portfolio, setPortfolio] = useState([]);
  const [totalValue, setTotalValue] = useState(0);

  const fetchPortfolioValue = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get("http://127.0.0.1:5000/api/portfolio/value", {
        headers: { Authorization: token },
      });
      setPortfolio(response.data.portfolio);
      setTotalValue(response.data.total_value);
    } catch (error) {
      console.error("Failed to fetch portfolio value:", error.response.data.message);
    }
  };

  useEffect(() => {
    fetchPortfolioValue();
  }, []);

  return (
    <div>
      <h2>Your Portfolio</h2>
      <p><strong>Total Value:</strong> ${totalValue}</p>
      <table>
        <thead>
          <tr>
            <th>Ticker</th>
            <th>Shares</th>
            <th>Current Price</th>
            <th>Total Value</th>
          </tr>
        </thead>
        <tbody>
          {portfolio.map((stock) => (
            <tr key={stock.ticker}>
              <td>{stock.ticker}</td>
              <td>{stock.shares}</td>
              <td>${stock.current_price}</td>
              <td>${stock.value}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Dashboard;
