import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  Chart as ChartJS,
  ArcElement,
  LineElement,
  CategoryScale,
  LinearScale,
  TimeScale,
  PointElement,
  Legend,
  Tooltip,
} from "chart.js";
import { Pie, Line } from "react-chartjs-2";

// Register Chart.js components
ChartJS.register(
  ArcElement,
  LineElement,
  CategoryScale,
  LinearScale,
  TimeScale,
  PointElement,
  Legend,
  Tooltip
);

const Dashboard = () => {
  const [portfolio, setPortfolio] = useState([]);
  const [totalValue, setTotalValue] = useState(0);
  const [historicalData, setHistoricalData] = useState({});

  const fetchHistoricalData = async (ticker) => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get(`http://127.0.0.1:5000/api/stock/history/${ticker}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setHistoricalData((prev) => ({
        ...prev,
        [ticker]: response.data,
      }));
    } catch (error) {
      console.error(`Error fetching historical data for ${ticker}:`, error.message);
    }
  };

  const fetchPortfolio = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get("http://127.0.0.1:5000/api/portfolio/value", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const portfolioData = Array.isArray(response.data.portfolio) ? response.data.portfolio : [];
      setPortfolio(portfolioData);
      setTotalValue(response.data.total_value || 0);

      // Fetch historical data for all stocks
      portfolioData.forEach((stock) => fetchHistoricalData(stock.ticker));
    } catch (error) {
      console.error("Error fetching portfolio:", error.message);
    }
  };

  useEffect(() => {
    fetchPortfolio();
  }, []);

  const renderPortfolioPieChart = (portfolio) => {
    const data = {
      labels: portfolio.map((stock) => stock.ticker),
      datasets: [
        {
          data: portfolio.map((stock) => stock.value),
          backgroundColor: [
            "rgba(75,192,192,0.6)",
            "rgba(255,99,132,0.6)",
            "rgba(54,162,235,0.6)",
            "rgba(255,206,86,0.6)",
            "rgba(153,102,255,0.6)",
          ],
          borderWidth: 1,
        },
      ],
    };
    return <Pie key="portfolio-pie-chart" data={data} />;
  };

  const renderPortfolioLineChart = (portfolio, historicalData) => {
    const dates = Object.keys(historicalData[portfolio[0]?.ticker] || {});
    const totalValues = dates.map((date) =>
      portfolio.reduce((sum, stock) => {
        const history = historicalData[stock.ticker];
        const priceOnDate = history?.find((entry) => entry.Date === date)?.Close || 0;
        return sum + priceOnDate * stock.shares;
      }, 0)
    );

    const data = {
      labels: dates,
      datasets: [
        {
          label: "Total Portfolio Value",
          data: totalValues,
          borderColor: "rgba(75,192,192,1)",
          backgroundColor: "rgba(75,192,192,0.2)",
          tension: 0.2,
        },
      ],
    };

    return <Line key="portfolio-line-chart" data={data} />;
  };

  return (
    <div>
      <h2>Your Portfolio</h2>
      <p><strong>Total Portfolio Value:</strong> ${totalValue.toLocaleString()}</p>

      <div style={{ display: "flex", justifyContent: "space-around" }}>
        <div>
          <h3>Portfolio Distribution</h3>
          {portfolio.length > 0 && renderPortfolioPieChart(portfolio)}
        </div>
        <div>
          <h3>Portfolio Performance Over Time</h3>
          {portfolio.length > 0 && Object.keys(historicalData).length > 0 &&
            renderPortfolioLineChart(portfolio, historicalData)}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
