import React, { useState } from "react";
import axios from "axios";

const StockPicker = () => {
  const [ticker, setTicker] = useState("");
  const [stockDetails, setStockDetails] = useState(null);
  const [shares, setShares] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  const searchStock = async () => {
    try {
      // Mock or real API call
      const response = await axios.get(`https://api.example.com/stocks/${ticker}`);
      setStockDetails(response.data);
      setErrorMessage("");
    } catch (error) {
      setStockDetails(null);
      setErrorMessage("Stock not found or API limit reached.");
    }
  };

  const addToPortfolio = async () => {
    try {
      // Add to portfolio API logic
      setSuccessMessage(`${shares} shares of ${ticker} added successfully!`);
      setErrorMessage("");
      setShares("");
    } catch (error) {
      setSuccessMessage("");
      setErrorMessage("Error adding stock to portfolio.");
    }
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      <h2>Add a Stock to Your Portfolio</h2>
      <div style={{ marginBottom: "20px" }}>
        <input
          type="text"
          placeholder="Enter Stock Ticker (e.g., AAPL)"
          value={ticker}
          onChange={(e) => setTicker(e.target.value.toUpperCase())}
          style={{ padding: "10px", marginRight: "10px", width: "300px" }}
        />
        <button onClick={searchStock} style={{ padding: "10px 20px" }}>
          Search
        </button>
      </div>

      {errorMessage && <p style={{ color: "red" }}>{errorMessage}</p>}

      {stockDetails && (
        <div style={{ marginBottom: "20px" }}>
          <h4>Stock Details:</h4>
          <p><strong>Company:</strong> {stockDetails.name || "N/A"}</p>
          <p>
            <strong>Current Price:</strong>{" "}
            {stockDetails.last_trade_price !== undefined
              ? `$${stockDetails.last_trade_price.toFixed(2)}`
              : "N/A"}
          </p>
        </div>
      )}

      {stockDetails && (
        <div>
          <input
            type="number"
            placeholder="Enter Number of Shares"
            value={shares}
            onChange={(e) => setShares(e.target.value)}
            style={{ padding: "10px", marginRight: "10px", width: "150px" }}
          />
          <button onClick={addToPortfolio} style={{ padding: "10px 20px" }}>
            Add to Portfolio
          </button>
        </div>
      )}

      {successMessage && <p style={{ color: "green" }}>{successMessage}</p>}
    </div>
  );
};

export default StockPicker;
